package JavaBasics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	//Primitive : int, char, double , boolean	
		//1.int
		
		int i =10;
		i=20;
		System.out.println(i);
		int j=30;
		System.out.println(i+j);
		//2.double
		double d =12.2;
		double d1 =36.6;
		double d3 =100;
		System.out.println(d3);
		
		//3.char : only single digit value, should write with in single quotes
		
		char c ='a';
		char c1='s';
		char c2 ='b';
		
		System.out.println(c);
		
		//4.boolean
		
		boolean b1 = true;
		boolean b2 = false;
		
		//5.String : string is a class, not a data type[but we can use as a data type to store values.]
		
		String s ="Hi";
		String s1 ="This is";
		String s2 ="Sreedhar";
		String s3 = "100";
		
		System.out.println(s);
		System.out.println(s+s1+s2+s3);

	}

}
